import React, { useRef } from "react";
import { PDFExport } from "@progress/kendo-react-pdf";
import { Button, Card, CardContent, Typography, Divider } from "@mui/material";

const PatientPDF = ({ form }) => {
  const pdfExportComponent = useRef(null);

  const handleExportWithComponent = () => {
    if (pdfExportComponent.current) {
      pdfExportComponent.current.save();
    }
  };

  return (
    <div>
      <Button
        variant="contained"
        color="primary"
        onClick={handleExportWithComponent}
        style={{ marginBottom: "10px" }}
      >
        Download PDF
      </Button>
      <PDFExport ref={pdfExportComponent} paperSize="A4">
        <Card style={{ padding: "20px", maxWidth: "800px", margin: "auto" }}>
          <CardContent>
            <Typography variant="h5" gutterBottom>
              Patient Report
            </Typography>
            
            <Divider />
            <Typography variant="h6">Patient Information</Typography>
            <Typography>Name: {form.first_name} {form.last_name}</Typography>
            <Typography>DOB: {new Date(form.dob).toLocaleDateString()}</Typography>
            <Typography>Address: {form.address}</Typography>
            <Typography>Post Code: {form.post_code}</Typography>
            <Typography>Phone: {form.phone}</Typography>
            <Typography>Email: {form.email}</Typography>
            <Typography>Notes: {form.notes}</Typography>
            
            <Divider style={{ margin: "10px 0" }} />
            <Typography variant="h6">Next of Kin</Typography>
            <Typography>Name: {form.kin_details.name}</Typography>
            <Typography>Address: {form.kin_details.address}</Typography>
            <Typography>Phone: {form.kin_details.phone}</Typography>
            <Typography>Email: {form.kin_details.email}</Typography>
            
            <Divider style={{ margin: "10px 0" }} />
            <Typography variant="h6">General Practitioner</Typography>
            <Typography>Name: {form.general_practitioner.name}</Typography>
            <Typography>Address: {form.general_practitioner.address}</Typography>
            <Typography>Phone: {form.general_practitioner.phone}</Typography>
            <Typography>Email: {form.general_practitioner.email}</Typography>
            
            <Divider style={{ margin: "10px 0" }} />
            <Typography variant="h6">Treatment Plan</Typography>
            <Typography>Concerns: {form.treatment_plan.patient_concerns}</Typography>
            <Typography>Goals: {form.treatment_plan.patient_goals}</Typography>
            <Typography>Plan: {form.treatment_plan.advised_plan}</Typography>
            <Typography>Expected Result: {form.treatment_plan.expected_result}</Typography>
            <Typography>
              Treatment Date: {new Date(form.treatment_plan.date).toLocaleDateString()}
            </Typography>
            <img src={form.treatment_plan.patient_sign} alt="Patient Signature" style={{ width: "100px", marginTop: "10px" }} />
            
            <Divider style={{ margin: "10px 0" }} />
            <Typography variant="h6">Treatment Records</Typography>
            {form.treatment_record.map((record) => (
              <Typography key={record._id}>{record.description} - ${record.amount}</Typography>
            ))}
            
            <Divider style={{ margin: "10px 0" }} />
            <Typography variant="h6">Images</Typography>
            <div style={{ display: "flex", gap: "10px", marginTop: "10px" }}>
              {form.before_images.map((img, index) => (
                <img key={index} src={img} alt="Before" style={{ width: "80px" }} />
              ))}
              {form.after_images.map((img, index) => (
                <img key={index} src={img} alt="After" style={{ width: "80px" }} />
              ))}
            </div>
            
            <Divider style={{ margin: "10px 0" }} />
            <Typography variant="h6">Further Treatment</Typography>
            {form.further_treatment.map((treatment, index) => (
              <img key={index} src={treatment.sign} alt="Further Treatment Signature" style={{ width: "100px" }} />
            ))}
            
            <Divider style={{ margin: "10px 0" }} />
            <Typography variant="h6">Patient Consent</Typography>
            <img src={form.patient_consent.sign} alt="Patient Consent Signature" style={{ width: "100px" }} />
            
            <Divider style={{ margin: "10px 0" }} />
            <Typography variant="h6">Side Effects</Typography>
            <Typography>Details: {form.side_effect.details || "N/A"}</Typography>
            
            <Divider style={{ margin: "10px 0" }} />
            <Typography variant="h6">Extra Notes</Typography>
            <Typography>{form.extra_notes || "N/A"}</Typography>
          </CardContent>
        </Card>
      </PDFExport>
    </div>
  );
};

export default PatientPDF;
